import React, {useState} from 'react'

export default function TextForm(props) {
    const [text, setText] = useState("");

    const changeToUppercase = () => {
        let newText = text.toUpperCase();
        setText(newText);
    }

    const changeToLowercase = () => {
        let newText = text.toLowerCase();
        setText(newText);
    }

    const changeHandle = (event) => {
        setText(event.target.value);
    }
    return (
        <>
        <div className="container">
            <h1>{props.heading}</h1>
            <div className="mb-3">
                <textarea className="form-control" value={text} onChange={changeHandle} id="myBox" rows="8"></textarea>
            </div>
            <button className="btn btn-primary mx-1" onClick={changeToUppercase}>Convert to UpperCase</button>
            <button className="btn btn-primary mx-1" onClick={changeToLowercase}>Convert to LowerCase</button>
        </div>

        <div className="container my-3">
            <h2>Preview</h2>

        </div>

        </>
    )
}
